mapper.select:
select id,fromId,toId,title,content,attachment,isread,createTime,isdelete from tb_mail
and isDelete=0
and toId=#{uid}
and title like CONCAT('%', #{search}, '%') or content like CONCAT('%', #{search}, '%') or attachment like CONCAT('%', #{search}, '%') or createTime like CONCAT('%', #{search}, '%') or isread like CONCAT('%', #{search}, '%')
order by ${sort} ${order}
limit #{offset},#{limit}

select id,fromId,toId,title,content,attachment,isread,createTime,isdelete from tb_mail
and isDelete=1
and toId=#{uid}
and title like CONCAT('%', #{search}, '%') or content like CONCAT('%', #{search}, '%') or attachment like CONCAT('%', #{search}, '%') or createTime like CONCAT('%', #{search}, '%') or isread like CONCAT('%', #{search}, '%')
order by ${sort} ${order}
limit #{offset},#{limit}
